start.addEventListener('click', async () => {
  start.style.display = 'none';
  if (!hasCameraPermission) {
      try {
          stream = await navigator.mediaDevices.getUserMedia({ 
              video: { 
                  width: { ideal: 480 }, 
                  height: { ideal: 640  }, 
                  frameRate: { ideal: 30, max: 60 } 
              }
          });
          hasCameraPermission = true;
          video.srcObject = stream;
          startButton.style.display = 'block';
          video.style.display = 'block';
      } catch (error) {
          console.error('Error accessing the camera', error);
      }
  } else {
      video.srcObject = stream;
      video.style.display = 'block';
  }
});




async function startCamera() {
    if (!hasCameraPermission) {
        try {
            stream = await navigator.mediaDevices.getUserMedia({ video: true });
            hasCameraPermission = true;
        } catch (error) {
            console.error('Error accessing the camera', error);
            return;
        }
    }
    video.srcObject = stream;
    video.style.display = 'block';
    timerDisplay.style.display = 'block';
    progressDisplay.style.display = 'block';
}






function stopCamera() {
    if (stream) {
        const tracks = stream.getTracks();
        tracks.forEach(track => track.stop());
        video.srcObject = null;
        video.style.display = 'none';
        timerDisplay.style.display = 'none';
        canvas.style.display = 'block';

        convertButton.style.display = 'block';

        progressDisplay.style.display = 'none';
        frame_button[0].style.display = 'block';
        frame_button[1].style.display = 'block';
        frame_button[2].style.display = 'block';
        frame_button[3].style.display = 'block';
        frame_button[4].style.display = 'block';
        frame_button[5].style.display = 'block';
        frame_button[6].style.display = 'block';
    }
}





function capturePhoto() {
    const photoCanvas = document.createElement('canvas');
    photoCanvas.width = 3000;
    photoCanvas.height = 4500;
    console.log(video.videoWidth,video.videoHeight)
    const photoCtx = photoCanvas.getContext('2d');
    photoCtx.drawImage(video, 0, 0, 3000, 4500);


    capturedPhotos.push(photoCanvas); // 캡처한 사진을 배열에 저장

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.drawImage(frameImage, 0, 0, canvas.width, canvas.height);

    for (let i = 0; i < capturedPhotos.length; i++) { // 배열을 순회하며 이미지 그리기
        const currentPos = positions[i];
        ctx.drawImage(capturedPhotos[i], 0, 0, photoCanvas.width, photoCanvas.height, currentPos.x, currentPos.y, currentPos.width, currentPos.height);
    }

    
    console.log(capturedPhotos)
    
    
    
    captureCount++;
    progressDisplay.textContent = `${captureCount}/4`;    
    if (captureCount >= 4) {
        clearInterval(captureInterval);
        clearInterval(countdownInterval);
        captureCount = 0;
        stopCamera();
    }
}



function updateCountdown() {
    countdown--;
    timerDisplay.textContent = countdown;

    if (countdown <= 0) {
        countdown = 6;

        // 애니메이션 효과 추가
        gridContainer.classList.add('flash');

        // 애니메이션 효과가 끝난 후 클래스를 제거
        setTimeout(() => {
            gridContainer.classList.remove('flash');
        }, 200); // 애니메이션 시간과 일치해야 함

        // 소리 재생
        const sound = document.getElementById('camera-sound');
        if (sound) {
            sound.play();
        }

        // 사진 찍기
        capturePhoto();
    }
}

startButton.addEventListener('click', () => {
    startButton.style.display = 'none';
    camera.style.boxShadow = '0 0 20px rgba(0, 0, 0, 1)';
    startCamera();
    if (countdownInterval) {
        clearInterval(countdownInterval);
    }
    captureCount = 0;
    countdown = 6;
    timerDisplay.textContent = countdown;
    countdownInterval = setInterval(updateCountdown, 1000);
    captureInterval = setInterval(() => {
        // capturePhoto가 호출되기 전에 애니메이션과 소리 처리가 되도록 updateCountdown에서 처리
        countdown = 6;
    }, 6000);
});








