convertButton.addEventListener('click', async () => {
    convertButton.style.display = 'none';
    loading.style.display = 'block';
    frame_button[0].style.display = 'none';
    frame_button[1].style.display = 'none';
    frame_button[2].style.display = 'none';
    frame_button[3].style.display = 'none';
    frame_button[4].style.display = 'none';
    frame_button[5].style.display = 'none';
    frame_button[6].style.display = 'none';

    const imageUrl = canvas.toDataURL('image/png');
    document.getElementById('imageUrl').innerText = imageUrl;
    const shortUrl = await uploadImageToImageBB(imageUrl);
    if (shortUrl) {
        loading.style.display = 'none';
        document.getElementById('imageUrl').innerText = shortUrl;
        generateQRCode(shortUrl);  // 업로드된 URL로 QR 코드 생성
    } else {
        console.error('Failed to upload image to Imgur');
    }
});

async function uploadImageToImageBB(imageBase64) {
    const url = 'https://api.imgbb.com/1/upload';
    const apiKey = '99022280c5493516d46632821953bc83';
    const formData = new FormData();
    formData.append('image', imageBase64.split(',')[1]);
    const response = await fetch(`${url}?key=${apiKey}`, {
        method: 'POST',
        body: formData
    });
    const data = await response.json();
    if (data.success) {
        return data.data.url;
    }
}

function generateQRCode(url) {
    const qrCodeContainer = document.getElementById('qrcode');
    qrCodeContainer.innerHTML = '';  // 기존 QR 코드 제거
    try {
        new QRCode(qrCodeContainer, {
            text: url,
            width: 256,
            height: 256
        });
    } catch (error) {
        console.error('Error generating QR code', error);
    }
}






